		   .attr("fill", "rgba(0.0, 100, 255, 0.2)")
		   .attr("class", "area-1");

		svg.selectAll(".line-2")
		   .data([data.slice(obj.i, obj.i+30)])
		   .join("path")
		   //.transition().duration(200)
		   .attr("d", path)
		   .attr("fill", "none")
		   .attr("stroke", "rgba(0.0, 255, 100, 0.7)")
		   .attr("stroke-width", "3")
		   .attr("class", "line-2");

		svg.selectAll(".line-3")
		   .data([data.slice(obj.i+30, obj.i+50)])
		   .join("path")
		   //.transition().duration(200)
		   .attr("d", path)
		   .attr("fill", "none")
		   .attr("stroke", "rgba(150, 100, 0, 1.0)")
		   .attr("stroke-width", "6")
		   .attr("class", "line-3");

		obj.i+=15;

		if (obj.i >= data.length + 50) { obj.i = 0; };
	}

	return obj;
</script>
""")

