# ‚ïî‚ïê‚ï° 5ae4147e-2a77-48bb-8651-7484a6605f2c
md"## Super composability example"

# ‚ïî‚ïê‚ï° d9a0df66-0ca9-4a87-84f3-2372d838dfc0
@bind new_sz_slider Slider(1:20)

# ‚ïî‚ïê‚ï° b0ba4d5e-b5de-49eb-af0c-999637179a5b
nnx = collect(1:new_sz_slider);

# ‚ïî‚ïê‚ï° 1e268675-dd14-4764-84eb-fe36564ef4c6
nny = rand(new_sz_slider);

# ‚ïî‚ïê‚ï° 101f24cf-d037-4552-aaf7-701a0af01bc6
line2 = Line(nnx, nny, "line_id";
	attributes=D3Attr(;
		attr=(;
			fill="none",
			#stroke="green",
			var"stroke-width"="3.0"
		),
		events=["click", "mouseover", "mousemove"],
		duration=200
	),
	curveType=Portinari.Natural
);

# ‚ïî‚ïê‚ï° 1d361c00-4f79-4717-a779-2aeb66440520
nctx = Context(
	(;domain=[extrema(nnx)...], range=[0, 690]),
	(;domain=[extrema(nny)...], range=[0, 300]),
	[line2],
	"ctx"
);

# ‚ïî‚ïê‚ï° a202d199-5fc3-4c88-91a6-15304809a282
begin
	rows = 16
	cols = 16
	ctxs = []
	rows_val = 1 / rows
	cols_val = 1 / cols

	row = Context(
		(domain=[0, 0], range=[0, 690]),
		(domain=[0, 0], range=[0, 300]),
		[Scale(Restyle(nctx, 
			D3Attr(;
				style=(;
					transform= i % 2 == 0 ? "scale(1, 1)" : "scale(-1, 1)",
					stroke="rgb($(rand() * 255), $(rand() * 255), $(rand() * 255))"
				)),
			"restyle-$i"
		), (i * cols_val, (i+1) * cols_val), (0.0, 1.0), "scale-$i") for i ‚àà (0:cols-1)] |> vec,
		"row"
	);

	Context(
		(domain=[0, 0], range=[0, 690]),
